import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { Validators } from '@angular/forms';
import { FormArray } from '@angular/forms';

@Component({
  selector: 'app-contact-form',
  templateUrl: './contact-form.component.html',
  styleUrls: ['./contact-form.component.scss']
})
export class ContactFormComponent {
  constructor(private fb: FormBuilder) { }

  contactForm = this.fb.group({
    name: ['', Validators.required],
  });

  onSubmit() {
    // TODO: Use EventEmitter with form value
    console.warn(this.profileForm.value);
  }
}
<form [formGroup]="contactForm" (ngSubmit)="onSubmit()">

    <div style="margin-bottom: 20px; width: 50%">
      <span class="rightText">How can we help?</span>
    </div>

  <label for="name">Name: </label>
  <input id="name" type="text" formControlName="name" required>

  <p>Complete the form to enable button.</p>
  <button type="submit" [disabled]="!profileForm.valid">Submit</button>
</form>

<hr>

<p>Form Value: {{ contactForm.value | json }}</p>

<p>Form Status: {{ contactForm.status }}</p>

<button type="button" (click)="onSubmit()">GetIn Touch</button>


