import { Component,OnInit } from '@angular/core';
import { FormArray,FormGroup,FormControl,Validators,FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-contact-form',
  templateUrl: './contact-form.component.html',
  styleUrls: ['./contact-form.component.scss']
})
export class ContactFormComponent implements OnInit{


  contactForm!: FormGroup;
  emailValidation: boolean = true;
  phoneValidation: boolean = true;
  contactdetails = {
    name: 'test',
    email: 'test@test.com',
    phone: '111-111-1111',
    company: 'FooBar',
    business: 'LorepIpsem',
  };
  ngOnInit(): void {
    this.contactForm = new FormGroup({
      name: new FormControl(this.contactdetails.name, [
        Validators.required,
        Validators.minLength(4),
      ]),
      email: new FormControl(this.contactdetails.email, [
        Validators.required,
        Validators.minLength(3),
        Validators.minLength(64),
      ]),
      phone: new FormControl(this.contactdetails.phone, [
        Validators.required,
        Validators.minLength(10),
        Validators.minLength(12),
      ]),
      company: new FormControl(this.contactdetails.company, [
        Validators.required,
        Validators.minLength(3),
        Validators.minLength(50),
      ]),
      business: new FormControl(this.contactdetails.business, [
        Validators.required,
        Validators.minLength(3),
        Validators.minLength(50),
      ]),
    });
  }
  get name() {
    return this.contactForm.get('name');
  }
  get phone() {
    return this.contactForm.get('phone');
  }
  get email() {
    return this.contactForm.get('email');
  }
  get company() {
    return this.contactForm.get('company');
  }
  get business() {
    return this.contactForm.get('business');
  }
  onSubmit(): void {
    console.log('Submitted!');
  }
}
<form [formGroup]="contactForm" (ngSubmit)="onSubmit()">

    <div class="container">
      <h1>How can we help?</h1>
    </div>
    <div class="formSections">
        <label for="name" class="required">Name:</label>
		<input type="text" minlength="3" maxlength="30" class="form-control" [class.is-invalid] = "contactForm!.get('name')!.invalid && contactForm!.get('name')!.touched"  formControlName="name" />
		<div
			*ngIf="(contactForm!.get('name')!.invalid && contactForm!.get('name')!.touched) || contactForm!.get('name')!.dirty">
			<small *ngIf="contactForm!.get('name')!.errors?.['required']" class="text-danger">Name is required</small>
		</div>
    </div>
    <div class="formSections">
        <label for="email" class="required">Email:</label>
        <input type="text" size="30" minlength="4" maxlength="64" class="form-control"  [class.is-invalid] = "contactForm!.get('email')!.invalid && contactForm!.get('email')!.touched" formControlName="email" [email]="emailValidation" />
        <div *ngIf="(contactForm!.get('email')!.invalid && contactForm!.get('email')!.touched) || contactForm!.get('email')!.dirty">
        <small class="text-danger" *ngIf="contactForm!.get('email')!.errors?.['required']">Email address is required</small>
        <small class="text-danger" *ngIf="contactForm!.get('email')!.errors?.['email']">Email address is invalid</small>
        </div>
    </div>
    <div class="formSections">
        <label for="phone" class="required">Phone:</label>
        <input type="tel" class="form-control" formControlName="phone" name="phone" pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}" required/>
    </div>
    <div class="formSections">
        <label for="company" class="required">Company Name:</label>
        <input type="text" size="20" minlength="5" maxlength="20" class="form-control" formControlName="company" name="company" required/>
    </div>
</form>
<button type="submit" class="submitButton" [disabled]="!contactForm.valid" (click)="onSubmit()">GET IN TOUCH</button>
